mapper.update:
update music_board
set M_RECOMMENTCNT = M_RECOMMENTCNT+1 
where m_no = #{m_no}



update music_board
set m_title = #{m_title}
, m_name = #{m_name}
,m_no = #{m_no}
,user_id = #{user_id}
,m_coverimage =#{m_coverimage}
,m_bpm = #{m_bpm}
, m_explain = #{m_explain}
,m_file =#{m_file}
,m_price =#{m_price}
,m_isfree =#{m_isfree}
,m_genre =#{m_genre}
, m_regdate = sysdate
Where m_no = #{m_no}
